function EQQ = EQQDistsample(r,n1,n2,n,lambda,Ngrid)  %The function computes the formula for EQQ term in the cartesian coordiantes   a = n1*n2'; %na.nb  b = n1*n; %na.nb  c = b(:,2);   d=kron(c,c'); %(na.n)(nb.n)  I=ones(Ngrid^2);%Ngrid^2 by Ngrid^2 matrix with all its elements equal to one   i = ones(Ngrid^2,1);  e = kron(c,i').^2+kron(i,c').^2; %(na.n)^2+(nb.n)^2  f=d.*a;%(na.n)(nb.n)(na.nb)  EQQ = lambda.*(35*(d.^2)-5*e+2*(a.^2)-20*f+I)/(r.^5);end   