function potEQQ = generateEQQsample(phi,theta,r,Ngrid,lambda)  % coordinates representing the distance between the monopoles          x = sin(theta).*cos(phi);  y = sin(theta).*sin(phi);  z = cos(theta);   disp('we are in genEQQ');  x = reshape(x,[Ngrid^2,1]);  y = reshape(y,[Ngrid^2,1]);  z = reshape(z,[Ngrid^2,1]);    n = [0,0,0;0,1,0;0,0,0]; %The unit vector joining the centres of the two dipoles as a matrix   moment1 = [x,y,z];             m1 = moment1;%The direction of the rotor  potEQQ = EQQDistsample(r,m1,m1,n,lambda,Ngrid);%dipole-dipole interaction energy as a matrix  %disp(potEQQ);end