clear all;ticNgrid = 15;         %number of grid points in any dimensionLmax = 1;           %basis set cut-off (spherical harmonics L)da = pi/Ngrid;                      %spehrical grid densityphi = 0:2*da:(2*pi-2*da);theta = 0:da:(pi-da);[phi,theta] = meshgrid(phi,theta);Nbasis = (Lmax+1)^2;P=2;[wfn,coeff,SphHarmCol] = wfnGrid(P); wfnlist = integratewfn(da,phi,theta,wfn,Ngrid,Nbasis);toc