function PotEngBasisEQQ = integrateEQQ(phi,theta,da,Ngrid,Nbasis,PotEngGridEQQ,SphHarmCol);  disp('we are in INTEQQ');  PotEngBasisEQQ = zeros(Nbasis,Nbasis);    ds1 = sin(theta)*2*da^2;  ds1(ds1==0) = 0.00000001;    ds2 = flip(ds1')';  ds2(ds2==0) = 0.00000001;    ds = kron(ds1,ds1);    %PotEngGridEQQ2 = flip(PotEngGridEQQ')';  PotEngGridDiagEQQ = diag(transpose(reshape(PotEngGridEQQ,[Ngrid^4,1])));  dsDiag = diag(reshape(ds,[Ngrid^4,1]));     PotEngBasisEQQ = conj(SphHarmCol)*(dsDiag*PotEngGridDiagEQQ)*transpose(SphHarmCol);end