clear all;Ngrid = 20;         %number of grid points in any dimensionLmax = 5;           %basis set cut-off (spherical harmonics L)da = pi/Ngrid;                      %spehrical grid densityphi = 0:2*da:(2*pi-2*da);theta = 0:da:(pi-da);[phi,theta] = meshgrid(phi,theta);Nbasis = (Lmax+1)^2;P=2;coeffname = ['CoeffPlot/coeff_EQQ','H2','.dat'];coefffid = fopen(coeffname,'w');fprintf(coefffid,'# SphHarm coeff \n');SphHarmCo = SphHarm(phi,theta,Lmax,Ngrid); [wfn,coeff,SphHarmCol] = wfnGrid(P); coeffplot = coeffBasis(da,phi,theta,wfn,Ngrid,Nbasis,SphHarmCo);fprintf(coefffid, '%d\n', coeffplot(:,5));fclose(coefffid);