function [sym,antisym,none] = symcheck(Lmax,P)    sym = [];  antisym = [];  none = [];  l = 5;  Nbasis = (1+l)^2;  Ngrid = 15;    [wfnOld,coeffBasis,SphHarmCol] = wfnGrid(P);      %swapping the elements of the coeffBasis and SphHarmCol matrix, equivalent to acting the exchange operator on the wavefunction     SphHarmTen = reshape(SphHarmCol,[Nbasis,Nbasis,Ngrid^4]);  coeffTen = reshape(coeffBasis,[Nbasis,Nbasis,Nbasis^2]);    SphHarmTentrans = permute(SphHarmTen,[2 1 3]);  coeffTentrans = permute(coeffTen,[2 1 3]);    SphHarmColnew = reshape(SphHarmTentrans,[Nbasis^2,Ngrid^4]);  coeffBasisnew = reshape(coeffTentrans,[Nbasis^2,Nbasis^2]);    wfnnew = transpose(coeffBasisnew)*SphHarmColnew;    %To avoid inequality due to computational error  wfnnew(wfnnew<=0.0000001)=0;   wfnOld(wfnOld<=0.0000001)=0;    %checking the symmetry of the eigenstates  for i=1:(Lmax+1)^4    if wfnnew(i,:) == wfnOld(i,:)      disp(['Eigenstate ',num2str(i),' is symmetric']);fflush(stdout);      sym = [sym,i];    else       if wfnnew(i,:) == -1*wfnOld(i,:)        disp(['Eigenstate ',num2str(i),' is antisymmetric']);fflush(stdout);        antisym = [antisym,i];      else         disp(['Eigenstate ',num2str(i),' is neither symmetric nor antisymmetric, but a combination of the two']);fflush(stdout);        none = [none,i];      end    end  endend    %coeffBasis(:,[2 5])=coeffBasis(:,[5 2]);  %coeffBasis(:,[3 6])=coeffBasis(:,[6 3]);  %coeffBasis(:,[4 7])=coeffBasis(:,[7 4]);  %coeffBasis(:,[9 11])=coeffBasis(:,[11 9]);  %coeffBasis(:,[10 14])=coeffBasis(:,[14 10]);  %coeffBasis(:,[13 15])=coeffBasis(:,[15 13]);  %#########################################%  %SphHarmCol([2 5],:)=SphHarmCol([5 2],:);  %SphHarmCol([3 6],:)=SphHarmCol([6 3],:);  %SphHarmCol([4 7],:)=SphHarmCol([7 4],:);  %SphHarmCol([9 11],:)=SphHarmCol([11 9],:);  %SphHarmCol([10 14],:)=SphHarmCol([14 10],:);  %SphHarmCol([13 15],:)=SphHarmCol([15 13],:);  %SphHarmColnew = SphHarmCol(1:16,:);  %coeffBasisnew = coeffBasis(1:16,:); 